import {connect} from 'react-redux'
import PostViewPage from '../../components/pages/PostViewPage'

/**
 * Get state from redux and map to properties of component
 *
 *  status - it's Form prop.
 * getButton will return some kind of state for this prop.
 */
function mapStateToProps(state) {
    console.log('PostViewPage');
    console.log(state);
    return {
        // state: state.postViewPage.slice(-1).pop()
        state: state.posts.filter(post => post.id === state.postViewPage.slice(-1).pop().id)
        //state: state.postViewPage
    }
}

export default connect(
    mapStateToProps,
)(PostViewPage)
