import React from 'react'

const form = (state = [], action) => {

    let formElement = state.filter(b => b.showElement === action.showElement).pop();
    switch (action.type) {
        case 'SHOW_FORM':
            if (formElement) {
                return [{
                    showElement: action.showElement,
                    elementAction: 'createPost',
                    enabled: !formElement.enabled
                }];
            }
            return [...state,
                {
                    showElement: action.showElement,
                    elementAction: 'createPost',
                    enabled: true
                }
            ];

        case 'SHOW_UPDATE_FORM':
            if (formElement) {
                return [{
                    showElement: 'form-element-1',
                    elementAction: 'updatePost',
                    optionalState: {
                        id: action.id,
                        title: action.title,
                        text: action.text
                    },
                    enabled: !formElement.enabled
                }];
            }
            return [...state,
                {
                showElement: 'form-element-1',
                elementAction: 'updatePost',
                optionalState: {
                    id: action.id,
                    title: action.title,
                    text: action.text
                },
                enabled: true
            }];
        default:
            return state
    }
};

export default form
